import { registerGeneratedAntlrLanguages, highlight } from '../src/index.js';

// Register languages first
registerGeneratedAntlrLanguages();

// Test simple HTML
const testHtml = '<div class="test">content</div>';
console.log('\n=== Testing HTML tokenization ===');
console.log('Input:', testHtml);

const result = highlight(testHtml, 'html', 'html');
console.log('\nHTML result:', result);

// Check what tokens are being generated by extracting class names
const tokenMatches = result.match(/tok-[a-z-]+/g);
console.log('\nToken types found:', [...new Set(tokenMatches)]);
